import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes, useNavigate } from "react-router-dom";


const App = () => {
  return (
    <Router>
      <div>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/signin" element={<SignIn />} />
          <Route path="/patients" element={<Patients />} />
          <Route path="/doctors" element={<Doctors />} />
        </Routes>
      </div>
    </Router>
  );
};

const Home = () => {
  return (
    <div>
      <Link to="/login">
        <button>Log in</button>
      </Link>
      <Link to="/signin">
        <button>Sign in</button>
      </Link>
    </div>
  );
};


const Login = () => {
  const [nume, setNume] = useState('');
  const [prenume, setPrenume] = useState('');
  const [email, setEmail] = useState('');
  const [parola, setParola] = useState('');
  const [submitClicked, setSubmitClicked] = useState(false);
  const history = useNavigate();

  useEffect(() => {
    if (submitClicked) {
      const fetchData = async () => {
        try {
          const data = { nume, prenume, email, parola };
          const response = await fetch('http://localhost:8080/authenticate', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
          });

          if (response.ok) {
            console.log('Autentificare reușită!', response);
            // Redirect către pagina "home" dacă răspunsul de la server este OK
            history.push('/'); // Ajustează ruta potrivit nevoilor tale
          } else {
            console.log('Autentificare eșuată!', response);
            // Poți gestiona altfel cazurile de eroare sau afișa un mesaj utilizatorului
          }
        } catch (error) {
          console.error('Eroare în timpul cererii către server:', error);
        }
      };

      fetchData();
    }
  }, [submitClicked, nume, prenume, email, parola, history]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted');
    setSubmitClicked(true);
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        {/* Inputurile pentru datele de autentificare */}
        <input
          type="text"
          name="nume"
          placeholder="Nume"
          value={nume}
          onChange={(e) => setNume(e.target.value)}
        />
        <input
          type="text"
          name="prenume"
          placeholder="Prenume"
          value={prenume}
          onChange={(e) => setPrenume(e.target.value)}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          name="parola"
          placeholder="Parola"
          value={parola}
          onChange={(e) => setParola(e.target.value)}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

const SignIn = () => {
  const [email, setEmail] = useState('');
  const [parola, setParola] = useState('');
  const [submitClicked, setSubmitClicked] = useState(false);
  const history = useNavigate();

  useEffect(() => {
    if (submitClicked) {
      const fetchData = async () => {
        try {
          const data = { email, parola };
          const response = await fetch('http://localhost:8080/signIn', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
          });

          if (response.ok) {
            console.log('Înregistrare reușită!', response);
            // Redirect către pagina "home" dacă răspunsul de la server este OK
            history.push('/');
          } else {
            console.log('Înregistrare eșuată!', response);
            // Poți gestiona altfel cazurile de eroare sau afișa un mesaj utilizatorului
          }
        } catch (error) {
          console.error('Eroare în timpul cererii către server:', error);
        }
      };

      fetchData();
    }
  }, [submitClicked, email, parola, history]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Formularul a fost trimis');
    setSubmitClicked(true);
  };

  return (
    <div>
      <h2>Înregistrare</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          name="parola"
          placeholder="Parola"
          value={parola}
          onChange={(e) => setParola(e.target.value)}
        />
        <button type="submit">Înregistrează-te</button>
      </form>
    </div>
  );
};

const Patients = () => {
  return (
    <div>
      <h2>Patients</h2>
      <Link to="/patients/personalinfo">
        <button>Personal Info</button>
      </Link>
      <Link to="/patients/medicalhistory">
        <button>Medical History</button>
      </Link>
      <Link to="/patients/doctorslist">
        <button>Doctors List</button>
      </Link>
      <Link to="/patients/appointments">
        <button>Appointments</button>
      </Link>
      <Link to="/patients/newappointment">
        <button>New Appointment</button>
      </Link>
    </div>
  );
};

const Doctors = () => {
  return (
    <div>
      <h2>Doctors</h2>
      <Link to="/doctors/personalinfo">
        <button>Personal Info</button>
      </Link>
      <Link to="/doctors/patientslist">
        <button>Patients List</button>
      </Link>
      <Link to="/doctors/appointments">
        <button>Appointments</button>
      </Link>
      <Link to="/doctors/consultations">
        <button>Consultations</button>
      </Link>
    </div>
  );
};
export default App;
