//import logo from './logo.svg';
import './App.css';

import { useState } from 'react';

function Square() {

  const [value, setValue] = useState(null);

  function handleClick() {
    setValue('X');
  }

  return (
    <button className="square" onClick={handleClick}> {value}
    </button>
  );
}

function schimbaContinut(resursa,jsFisier,jsFunctie){



  var xhttp=new XMLHttpRequest();
  xhttp.onreadystatechange=function(){
    document.getElementById("continut").innerHTML=this.responseText;

    if (jsFisier) {
      var elementScript = document.createElement('script');
      elementScript.onload = function () {
      console.log("hello");
      if (jsFunctie) {
      window[jsFunctie]();
      }
      };
      elementScript.src = jsFisier;
      document.head.appendChild(elementScript);
      } else {
      if (jsFunctie) {
      window[jsFunctie]();
      }
    }

    if(resursa == "invat"){
      browser();
    }
  };

  xhttp.open("GET", resursa + ".html", true);
  xhttp.send();

}

function Indexu(){

  return(
    <>

    <head>
      <title>Home</title>
    </head>


    <body onload="schimbaContinut('acasa')">

	    <nav>		
            <a onclick="schimbaContinut('acasa')"><b>Acasă</b></a>
            <a onclick="schimbaContinut('inregistreaza')"><b>Înregistrează</b></a>
            <a onclick="schimbaContinut('video')"><b>Video</b></a>
            <a onclick="schimbaContinut('invat')"><b>Învăț</b></a>
	          <a onclick="schimbaContinut('despre')"><b>Despre</b></a>
            <a onclick="schimbaContinut('persoane', 'js/persoane.js', 'incarcaPersoane')"><b>Persoane</b></a>
            <a onclick="schimbaContinut('cumparaturi')"><b>Cumparături</b></a>

	    </nav>

    <main id = "continut"></main>

    </body>

    </>
  );
}

export default function App() {
  return (
    <>
      <Indexu/>
      <Square/>
    </>
  );
}
