import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Routes, useNavigate } from "react-router-dom";

const InfoComponent = () => {
  // ...
  // Componenta care afișează informațiile utilizatorului după autentificare
};

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <Router>
      <div>
        <h1>Users</h1>
        {!isLoggedIn ? (
          <div>
            <Link to="/login">
              <button>Log in</button>
            </Link>
            <Link to="/signin">
              <button>Sign in</button>
            </Link>
            <Routes>
              <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
              <Route path="/signin" element={<SignIn />} />
            </Routes>
          </div>
        ) : (
          <div>
            <InfoComponent />
          </div>
        )}
      </div>
    </Router>
  );
};

const Login = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logica de autentificare (de exemplu, verificarea datelor introduse)

    // Dacă autentificarea este reușită, setează isLoggedIn pe true
    setIsLoggedIn(true);
    navigate('/');
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        {/* Inputurile pentru datele de autentificare */}
        <input type="text" placeholder="Nume" />
        <input type="text" placeholder="Prenume" />
        <input type="email" placeholder="Email" />
        <input type="password" placeholder="Parola" />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

const SignIn = () => {
  // Component pentru procesul de înregistrare (Sign in)
  return (
    <div>
      <h2>Sign in</h2>
      {/* Formular pentru înregistrare */}
    </div>
  );
};

export default App;
